import React from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import Grid from "@material-ui/core/Grid";
import dashboardStyles from "styles/dashboard";
import { makeStyles } from "@material-ui/core/styles";
import Product from "components/Product";

const useStyles = makeStyles(theme => dashboardStyles(theme));

const Cart = props => {
  const { cartItems } = props,
    classes = useStyles();
  const addedCartItems = cartItems.map(eachProduct => {
    return (
      <Grid key={`grid-${eachProduct.id}`} item xs={3}>
        <Product
          key={eachProduct.id}
          productInfo={eachProduct}
          onClickHandler={() => {
            addToCart(eachProduct);
          }}
        />
      </Grid>
    );
  });

  return (
    <Grid className={classes.container} container spacing={3}>
      {addedCartItems}
    </Grid>
  );
};

Cart.propTypes = {
  cartItems: PropTypes.array
};

const mapStateToProps = ({ cartReducer }) => {
  const { cartItems } = cartReducer;

  return {
    cartItems
  };
};

export default connect(mapStateToProps)(memo(Cart));
