import React from "react";
import Card from "@material-ui/core/Card";
import totalCardStyles from "styles/totalCard";
import { makeStyles } from "@material-ui/core/styles";
import { getTotal } from "utils/helper";

const useStyles = makeStyles(theme => totalCardStyles(theme));

const TotalCard = props => {
  const { cartItems } = props,
    classes = useStyles(),
    itemsTotal = cartItems.map(item => {
      return (
        <div className={classes.totalCard}>
          {" "}
          <span></span>{item.name} {item.currency} {item.price} * {item.quantity} ={" "}
          {item.price * item.quantity}
        </div>
      );
    });
  return (
    <Card >
      {itemsTotal}
      <div>Total:{getTotal(cartItems)}</div>
    </Card>
  );
};

export default TotalCard;
