import { cartActionTypes } from "utils/actionTypes";

const { ADD_TO_CART, REMOVE_FROM_CART } = cartActionTypes;

const initialState = {
  cartItems: []
};

export default (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case ADD_TO_CART:
      const cartItems = [...state.cartItems];
      const previousEntryOfItem = cartItems.filter(
        cartItem => cartItem.id === payload.id
      );
      if (previousEntryOfItem.length) {
        previousEntryOfItem[0].quantity += 1;
      } else {
        payload.quantity = 0;
        cartItems.push(payload);
      }
      return {
        ...state,
        cartItems
      };

    case REMOVE_FROM_CART:
      return {
        ...state,
        cartItems: payload
      };
    default:
      return state;
  }
};
