import PropTypes from "prop-types";
import React, { memo } from "react";
import { connect } from "react-redux";
import Card from "@material-ui/core/Card";
import cartProductStyles from "styles/cartProduct";
import CardMedia from "@material-ui/core/CardMedia";
import { makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import CardActions from "@material-ui/core/CardActions";
import Button from "@material-ui/core/Button";
import { getQuantities, getTotal } from "utils/helper";
import ButtonGroup from "@material-ui/core/ButtonGroup";
import { addToCart } from "actions/cart/cartActions";

const useStyles = makeStyles(theme => cartProductStyles(theme));

const CartProduct = props => {
  const { productInfo, cartItems, addToCart } = props,
    { name, image, price, currency } = productInfo;

  const classes = useStyles();

  return (
    <Card className={classes.card}>
      <CardMedia
        className={classes.media}
        image={require(`assets/images/${image}`)}
        alt={image}
        title={image}
      />

      <CardActions className={classes.actionItems}>
        <Typography className={classes.price} component="span">
          {currency}{price}
        </Typography>
        <Typography className={classes.qtyContainer} component="div">
          <Typography component="span">Qty</Typography>
          <ButtonGroup size="small" className={classes.buttonGroup}>
            <Button onClick={addToCart}>+</Button>
            <Typography component="span">{getQuantities(cartItems)}</Typography>
            <Button>-</Button>
          </ButtonGroup>
        </Typography>
      </CardActions>
      <Typography component="div">
        <Typography component="span" className={classes.productName}>
          {name}
        </Typography>
        <Typography component="div" className={classes.totalContainer}>
          <Typography component="span" className={classes.leftSpacing}>
            Total
          </Typography>
          <Typography component="span" className={classes.leftSpacing}>
            {productInfo.quantity * productInfo.price}
          </Typography>
        </Typography>
      </Typography>
    </Card>
  );
};

CartProduct.propTypes = {
  cartItems: PropTypes.array
};

const mapDispatchToProps = dispatch => ({
  addToCart: cartProduct => dispatch(addToCart(cartProduct))
});

const mapStateToProps = ({ cartReducer }) => {
  const { cartItems } = cartReducer;

  return {
    cartItems
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(memo(CartProduct));
