import PropTypes from "prop-types";
import React, { memo } from "react";
import appBarStyles from "styles/shoppingAppBar";
import { connect } from "react-redux";
import Badge from "@material-ui/core/Badge";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import IconButton from "@material-ui/core/IconButton";
import { Link } from "react-router-dom";

const useStyles = makeStyles(theme => appBarStyles(theme));

const ShoppingAppBar = props => {
  const { cartItems } = props,
    classes = useStyles();

  const cartQuantityAddup = cartItems.reduce(
    (sum, item) => sum + item.quantity,
    0
  );

  return (
    <AppBar className={classes.appBar} position="static">
      <Toolbar>
        <Typography variant="h6" className={classes.title}>
          <Link to="/dashboard">AegonLife Shopping</Link>
        </Typography>
        <IconButton
          aria-label="shows number of items added in cart"
          color="inherit">
          <Badge badgeContent={cartQuantityAddup} color="secondary">
            <img
              alt="shoppingCartImg"
              className={classes.image}
              src={require("assets/images/shoppingCart.png")}
            />
          </Badge>
        </IconButton>
      </Toolbar>
    </AppBar>
  );
};

ShoppingAppBar.propTypes = {
  cartItems: PropTypes.array
};

const mapStateToProps = ({ cartReducer }) => {
  const { cartItems } = cartReducer;

  return {
    cartItems
  };
};

export default connect(mapStateToProps)(memo(ShoppingAppBar));
