import React, { useEffect, useState } from "react";
import Grid from "@material-ui/core/Grid";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import IconButton from "@material-ui/core/IconButton";
import Badge from "@material-ui/core/Badge";
import { makeStyles } from "@material-ui/core/styles";
import Product from "components/Product";
import { getProducts } from "./services/apis";
import appStyles from "styles/app";

const useStyles = makeStyles(theme => appStyles(theme));

const App = () => {
  const [products, setProducts] = useState([]),
    [cart, addToCart] = useState([]);

  const classes = useStyles();

  useEffect(() => {
    getProducts()
      .then(response => {
        const { data: products } = response;
        setProducts(products);
      })
      .catch(err => {
        console.error("Error:", err);
      });
  }, []);

  const productsList = products.map((eachProduct, index) => {
    return (
      <Grid key={`grid-${eachProduct.id}`} item xs={3}>
        <Product
          key={eachProduct.id}
          productInfo={eachProduct}
          onClickHandler={() => {
            addToCart(eachProduct);
          }}
        />
      </Grid>
    );
  });

  return (
    <div className="App">
      <AppBar className={classes.appBar} position="static">
        <Toolbar>
          <Typography variant="h6" className={classes.title}>
            AegonLife Shopping
          </Typography>
          <IconButton
            aria-label="shows number of items added in cart"
            color="inherit">
            <Badge badgeContent={4} color="secondary">
              <img
                className={classes.image}
                alt="shoppingCartImg"
                src={require("assets/images/shoppingCart.png")}
              />
            </Badge>
          </IconButton>
        </Toolbar>
      </AppBar>
      <Grid className={classes.container} container spacing={3}>
        {productsList}
      </Grid>
    </div>
  );
};

export default App;
