import appStyles from "styles/app";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import Product from "components/Product";
import Grid from "@material-ui/core/Grid";
import Badge from "@material-ui/core/Badge";
import { getProducts } from "./services/apis";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import { addToCart } from "actions/cart/cartActions";
import Typography from "@material-ui/core/Typography";
import IconButton from "@material-ui/core/IconButton";
import { makeStyles } from "@material-ui/core/styles";
import { setProducts } from "actions/product/productActions";
import React, { Fragment, useEffect, memo } from "react";

const useStyles = makeStyles(theme => appStyles(theme));

const App = props => {
  const { setProducts, products, addToCart, cartItems } = props;

  const classes = useStyles();

  useEffect(() => {
    const callProducts = async () => {
      try {
        const response = await getProducts(),
          { data: products } = response;
        setProducts(products);
      } catch (error) {
        console.error("Error:", error);
      }
    };

    callProducts();
  }, []);

  const productsList = products.map((eachProduct, index) => {
    return (
      <Grid key={`grid-${eachProduct.id}`} item xs={3}>
        <Product
          key={eachProduct.id}
          productInfo={eachProduct}
          onClickHandler={() => {
            addToCart(eachProduct);
          }}
        />
      </Grid>
    );
  });

  const cartQuantityAddup = cartItems.reduce(
    (sum, item) => sum + item.quantity,
    0
  );

  return (
    <Fragment>
      <AppBar className={classes.appBar} position="static">
        <Toolbar>
          <Typography variant="h6" className={classes.title}>
            AegonLife Shopping
          </Typography>
          <IconButton
            aria-label="shows number of items added in cart"
            color="inherit">
            <Badge badgeContent={cartQuantityAddup} color="secondary">
              <img
                alt="shoppingCartImg"
                className={classes.image}
                src={require("assets/images/shoppingCart.png")}
              />
            </Badge>
          </IconButton>
        </Toolbar>
      </AppBar>
      <Grid className={classes.container} container spacing={3}>
        {productsList}
      </Grid>
    </Fragment>
  );
};

App.propTypes = {
  addToCart: PropTypes.func,
  products: PropTypes.array,
  cartItems: PropTypes.array,
  setProducts: PropTypes.func
};

const mapStateToProps = ({ productReducer, cartReducer }) => {
  const { products } = productReducer,
    { cartItems } = cartReducer;

  return {
    products,
    cartItems
  };
};

const mapDispatchToProps = dispatch => ({
  setProducts: productsList => dispatch(setProducts(productsList)),
  addToCart: cartProduct => dispatch(addToCart(cartProduct))
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(memo(App));
