import React, { useEffect, useState } from "react";
import Product from "./components/Product";
import { getProducts } from "./services/apis";
import "./App.css";

function App() {
  const [products, setProducts] = useState([]),
    [cart, addToCart] = useState([]);

  useEffect(() => {
    getProducts()
      .then(res => {
        setProducts(res);
      })
      .catch(err => {
        console.error("Error:", err);
      });
  }, []);

  const productsList = products.map((eachProduct, index) => {
    return (
      <Product
        key={eachProduct.id}
        productInfo={eachProduct}
        onClickHandler={addToCart}
      />
    );
  });

  return <div className="App">{productsList}</div>;
}

export default App;
