import { productActionTypes } from "utils/actionTypes";

const { SET_PRODUCTS } = productActionTypes;

const initialState = {
  cartItems: []
};

export default (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case ADD_TO_CART:
      return {
        ...state,
        cartItems: payload
      };
    case REMOVE_FROM_CART:
      return {
        ...state,
        cartItems: payload
      };
    default:
      return state;
  }
};
