import { cartActionTypes } from "utils/actionTypes";

const { ADD_TO_CART, REMOVE_CART_ITEM, DECREASE_QUANTITY } = cartActionTypes;

const initialState = {
  cartItems: []
};

export default (state = initialState, action) => {
  const { type, payload } = action,
    cloneCartItems = [...state.cartItems];
  switch (type) {
    case ADD_TO_CART: {
      const previousEntryOfItem = cloneCartItems.findIndex(
        cartItem => cartItem.id === payload.id
      );

      if (previousEntryOfItem > -1) {
        cloneCartItems[previousEntryOfItem].quantity += 1;
      } else {
        payload.quantity = 1;
        cloneCartItems.push(payload);
      }

      return {
        ...state,
        cartItems: cloneCartItems
      };
    }

    case REMOVE_CART_ITEM: {
      return {
        ...state,
        payload
      };
    }

    case DECREASE_QUANTITY: {
      const productItem = cloneCartItems.findIndex(
        cartItem => cartItem.id === payload.id
      );

      cloneCartItems[productItem].quantity -= 1;

      return {
        ...state,
        cartItems: cloneCartItems
      };
    }

    default:
      return state;
  }
};
