import { cartActionTypes } from "utils/actionTypes";

const { ADD_TO_CART, REMOVE_FROM_CART } = cartActionTypes;

const initialState = {
  cartItems: []
};

export default (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case ADD_TO_CART: {
      const cloneCartItems = [...state.cartItems],
        previousEntryOfItem = cloneCartItems.findIndex(
          cartItem => cartItem.id === payload.id
        );

      if (previousEntryOfItem > -1) {
        cloneCartItems[previousEntryOfItem].quantity += 1;
      } else {
        payload.quantity = 1;
        cloneCartItems.push(payload);
      }

      return {
        ...state,
        cartItems: cloneCartItems
      };
    }

    case REMOVE_FROM_CART:
      return {
        ...state,
        payload
      };

    default:
      return state;
  }
};
