import React, { useEffect, useState } from "react";
import Product from "components/Product";
import { getProducts } from "./services/apis";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import IconButton from "@material-ui/core/IconButton";
import Badge from "@material-ui/core/Badge";
import "./App.css";

const useStyles = makeStyles(theme => ({
  title: {
    flexGrow: 1,
    display: "none",
    [theme.breakpoints.up("sm")]: {
      display: "block"
    },
    color: "rgba(0, 0, 0, 0.87)"
  },
  image: {
    height: "30px"
  },
  container: {
    margin: "15px"
  },
  appbar: {
    backgroundColor: "#5eb3ce"
  }
}));

function App() {
  const [products, setProducts] = useState([]),
    [cart, addToCart] = useState([]);

  const classes = useStyles();

  useEffect(() => {
    getProducts()
      .then(res => {
        setProducts(res);
      })
      .catch(err => {
        console.error("Error:", err);
      });
  }, []);

  const productsList = products.map((eachProduct, index) => {
    return (
      <Grid item xs={3}>
        <Product
          key={eachProduct.id}
          productInfo={eachProduct}
          onClickHandler={() => {
            addToCart(eachProduct);
          }}
        />
      </Grid>
    );
  });

  return (
    <div className="App">
      <AppBar className={classes.appbar} position="static">
        <Toolbar>
          <Typography variant="h6" className={classes.title}>
            AegonLife Shopping
          </Typography>
            <IconButton aria-label="shows number of items added in cart" color="inherit">
              <Badge badgeContent={4} color="secondary">
                <img className={classes.image} src={require("assets/images/shoppingCart.png")}/>
              </Badge>
            </IconButton>
        </Toolbar>
      </AppBar>
      <Grid className={classes.container} container spacing={3}>
        {productsList}
      </Grid>
    </div>
  );
}

export default App;
