import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
// import watch from 'assets/images/watch.png';
import "./products.css";

const useStyles = makeStyles(theme => ({
  card: {
    maxWidth: 345
  },
  media: {
    height: 0,
    paddingTop: "56.25%" // 16:9
  },
  cardHeader: {
    backgroundColor: "#4a8ac8",
    fontSize: "8px"
  },
  price: {
    fontWeight: 900
  }
}));

const Product = props => {
  const { productInfo, onClickHandler } = props,
    { name, image, price, description } = productInfo;

  const classes = useStyles();

  return (
    <Card className={classes.card}>
      <CardHeader className={classes.cardHeader} title={name} />
      <CardMedia
        className={classes.media}
        image={require(`assets/images/${image}`)}
        alt={image}
        title={image}
      />
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
          {description}
        </Typography>
      </CardContent>
      <CardActions>
        <Typography className={classes.price} component="span">
          {price}
        </Typography>
        <Button  variant="contained" color="primary">
          Add to Cart
        </Button>
      </CardActions>
    </Card>
  );
};

export default Product;
